import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { GLOBAL } from 'src/app/services/GLOBA';
import { GuestService } from 'src/app/services/guest.service';
declare var $:any;
declare var iziToast:any;
declare var paypal:any;

interface HtmlInputEvent extends Event{
  target : HTMLInputElement & EventTarget;
} 

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit {

  @ViewChild('paypalButton',{static:true}) paypalElement : ElementRef;

  public direcciones :Array<any> = [];
  public token = localStorage.getItem('token');
  public load_data = false;
  public direccion : any = {
    pais: '',
    region: '',
    provincia: '',
    distrito: '',
    principal: false,
    zona: ''
  };

  public geo : any = {};
  public country = '';
  public currency = 'PEN';

  public regiones:Array<any> = [];
  public provincias:Array<any> = [];
  public distritos:Array<any> = [];

  public regiones_arr:Array<any> = [];
  public provincias_arr:Array<any> = [];
  public distritos_arr:Array<any> = [];

  public zonas:Array<any> = [];
  public str_pais = '';
  public carrito_arr : Array<any> = [];
  public user_lc : any = undefined;  
  public subtotal = 0;
  public envio = 0;
  public total_pagar = 0;
  public url = GLOBAL.url;

  public direccion_envio : any = undefined;
  public metodo_pago = '';
  public btn_load = false;

  /***************************** */

  public venta : any = {};
  public dventa : Array<any> = [];
  public nota = '';
  public tipo_descuento = undefined;
  public valor_descuento = 0;

   /***************************** */

   public descuento = 0;
   public total_pagar_const = 0;
   public config: any = {};
   public envio_gratis= false;

  constructor(
    private _guestService:GuestService,
    private _router:Router
  ) { 

    let obj_lc :any= localStorage.getItem('user_data');
    this.user_lc = JSON.parse(obj_lc);
    let lc_geo :any= localStorage.getItem('geo');
    this.geo = JSON.parse(lc_geo);
    this.country = this.geo.country_name;
    this.currency = this.geo.currency;

    this._guestService.get_Regiones().subscribe(
      response=>{
        this.regiones_arr = response;
      }
    );

    this._guestService.get_Procincias().subscribe(
      response=>{
        this.provincias_arr = response;
      }
    );

    this._guestService.get_Distritos().subscribe(
      response=>{
        this.distritos_arr = response;
      }
    );

    this._guestService.get_Zonas().subscribe(
      response=>{
        let respuesta :Array<any> = response;
        this.zonas = respuesta.sort(function (a, b) {
          if (a.country > b.country) {
            return 1;
          }
          if (a.country < b.country) {
            return -1;
          }
          // a must be equal to b
          return 0;
        }); 
      }
    );
  }

  ngOnInit(): void {
    this.obtener_direccion();
    this.obtener_carrito();

    this._guestService.obtener_config_admin().subscribe(
      response=>{
        this.config = response.data;
        console.log(this.config);

      }
    );

    console.log(this.direccion_envio);


    if(this.currency == 'PEN'){
      if(this.direccion_envio == undefined){
        if(this.carrito_arr.length == 0){
          setTimeout(() => {
            $('.contpaypal').addClass('class','cont-paypal');
          }, 100);
        }
      }

    }


    paypal.Buttons({
      style: {
          layout: 'horizontal',
          tagline : true
      },

      createOrder: (data:any,actions:any)=>{
        return actions.order.create({
          purchase_units : [{
            description : 'Pago en Pragol',
            amount : {
              currency_code : 'USD',
              value: this.total_pagar
            },
          }]
        });

      },
      onApprove : async (data:any,actions:any)=>{


      },
      onError : (err:any) =>{

      },
      onCancel: function (data:any, actions:any) {

      }
    }).render(this.paypalElement.nativeElement);
  }

  obtener_direccion(){
    this._guestService.obtener_direccion_todos_cliente(localStorage.getItem('_id'),this.token).subscribe(
      response=>{
        this.direcciones = response.data;
        console.log(this.direcciones);

        this.load_data = false;
      }
    );
  }

  select_pais(){
    let str_select_pais = this.str_pais.split('_');
    let pais =str_select_pais[0];
    this.direccion.zona = str_select_pais[1];
    this.direccion.pais = pais;

    if(pais == 'Perú'){
      setTimeout(() => {
        $('#sl-region').prop('disabled', false);
      }, 50);
      this._guestService.get_Regiones().subscribe(
        response=>{
          console.log(response);
          response.forEach((element:any) => {
            this.regiones.push({
              id: element.id,
              name: element.name
            });
          });

        }
      );
    }else{
      setTimeout(() => {
        $('#sl-region').prop('disabled', true);
        $('#sl-provincia').prop('disabled', true);
        $('#sl-distrito').prop('disabled', true);
      }, 50);
      this.regiones = [];
      this.provincias = [];
      this.distritos = [];

      this.direccion.region = '';
      this.direccion.provincia = '';
      this.direccion.distrito = '';

    }
  }

  select_region(){
    this.provincias = [];
    setTimeout(() => {
      $('#sl-provincia').prop('disabled', false);
      $('#sl-distrito').prop('disabled', true);
    }, 50);
    this.direccion.provincia = '';
    this.direccion.distrito = '';
    this._guestService.get_Procincias().subscribe(
      response=>{
        response.forEach((element:any) => {
            if(element.department_id == this.direccion.region){
              this.provincias.push(
                element
              );
            }
        });
        console.log(this.provincias);


      }
    );
  }

  select_provincia(){
    this.distritos = [];
    setTimeout(() => {
      $('#sl-distrito').prop('disabled', false);
    }, 50);
    this.direccion.distrito= '';
    this._guestService.get_Distritos().subscribe(
      response=>{
        response.forEach((element:any) => {
          if(element.province_id == this.direccion.provincia){
            this.distritos.push(
              element
            );
          }
      });
      console.log(this.distritos);

      }
    );
  }

  registrar(registroForm:any){
    if(registroForm.valid){

      this.regiones_arr.forEach(element => {
        if(parseInt(element.id) == parseInt(this.direccion.region)){
          this.direccion.region = element.name;
        }
      });

      this.provincias_arr.forEach(element => {
        if(parseInt(element.id) == parseInt(this.direccion.provincia)){
          this.direccion.provincia = element.name;
        }
      });

      this.distritos_arr.forEach(element => {
        if(parseInt(element.id) == parseInt(this.direccion.distrito)){
          this.direccion.distrito = element.name;
        }
      });

      let data = {
        nombres: this.direccion.nombres,
        apellidos: this.direccion.nombres,
        dni: this.direccion.dni,
        zip: this.direccion.zip,
        direccion: this.direccion.direccion,
        referencia: this.direccion.referencia,
        telefono: this.direccion.telefono,
        pais: this.direccion.pais,
        zona: this.direccion.zona,
        region: this.direccion.region,
        provincia: this.direccion.provincia,
        distrito: this.direccion.distrito,
        principal: this.direccion.principal,
        cliente: localStorage.getItem('_id')
      }

      console.log(data);
      this._guestService.registro_direccion_cliente(data,this.token).subscribe(
        response=>{
          this.direccion = {
            pais: '',
            region: '',
            provincia: '',
            distrito: '',
            principal: false,
            referencia: ''
          };
          $('#sl-region').prop('disabled', true);
          $('#sl-provincia').prop('disabled', true);
          $('#sl-distrito').prop('disabled', true);
          this.obtener_direccion();
          iziToast.show({
              title: 'SUCCESS',
              titleColor: '#1DC74C',
              color: '#FFF',
              class: 'text-success',
              position: 'topRight',
              message: 'Se agregó la nueva direccion correctamente.'
          });
        }
      );


    }else{
      iziToast.show({
        title: 'ERROR',
        titleColor: '#FF0000',
        color: '#FFF',
        class: 'text-danger',
        position: 'topRight',
        message: 'Los datos del formulario no son validos'
    });
    }
  }

  obtener_carrito(){
    this._guestService.obtener_carrito_cliente(this.user_lc._id,this.token).subscribe(
      response=>{
        this.carrito_arr = response.data;
        this.carrito_arr.forEach(element => { 
            if(this.currency != 'PEN'){
              this.dventa.push({
                producto: element.producto._id,
                subtotal: element.producto.precio_dolar,
                variedad: element.variedad._id,
                cantidad: element.cantidad,
                cliente: localStorage.getItem('_id')
              });
            }else if(this.currency == 'PEN'){
              this.dventa.push({
                producto: element.producto._id,
                subtotal: element.producto.precio,
                variedad: element.variedad._id,
                cantidad: element.cantidad,
                cliente: localStorage.getItem('_id')
              });
            }  
        });
        this.calcular_carrito();
      }
    );
  }

  calcular_carrito(){
    this.subtotal = 0;
    if(this.currency == 'PEN'){
      this.carrito_arr.forEach(element => {
          let sub_precio = parseInt(element.producto.precio) * element.cantidad;
          this.subtotal = this.subtotal + sub_precio;
      });
    }else{
      this.carrito_arr.forEach(element => {
          let sub_precio = parseInt(element.producto.precio_dolar) * element.cantidad;
          this.subtotal = this.subtotal + sub_precio;
      });
    }

    this.total_pagar = this.subtotal;
    this.total_pagar_const = this.subtotal;
  }

  select_direccion_envio(item:any){
    this.envio_gratis = false;
    this.direccion_envio = item;
    if(this.direccion_envio.pais != 'Perú'){
      if(this.direccion_envio.zona == 'Zona 1'){
        this.envio = 12;
      }else if(this.direccion_envio.zona == 'Zona 2'){
        this.envio = 18;
      }else if(this.direccion_envio.zona == 'Zona 3'){
        this.envio = 24;
      }else if(this.direccion_envio.zona == 'Zona 4'){
        this.envio = 35;
      }
    }else if(this.direccion_envio.pais == 'Perú'){
      if(this.direccion_envio.region == 'Lima'){
        this.envio = 10;
      }else if(this.direccion_envio.region != 'Lima'){
        this.envio = 15;
      }
    }

    if(this.currency == 'PEN'){
      if(this.total_pagar >= this.config.monto_min_soles){
        this.envio = 0;
        this.envio_gratis = true;
      }
    }else if(this.currency != 'PEN'){
      if(this.total_pagar >= this.config.monto_min_dolares){
        this.envio = 0;
        this.envio_gratis = true;
      }
    }

    if(this.venta.cupon != undefined){
      this.total_pagar = (this.total_pagar_const -this.descuento)+this.envio;
    }else{
      this.total_pagar = this.total_pagar_const +this.envio;
    }

  }

  get_token_culqi(){
    this._guestService.comprobar_carrito_cliente({detalles:this.dventa},this.token).subscribe(
      response=>{
        if(response.venta){
          let items = [];
          console.log(this.descuento);

          this.carrito_arr.forEach(element => {
            items.push({
              title: element.producto.titulo,
              description: element.producto.descripcion,
              quantity: element.cantidad,
              currency_id: 'PEN',
              unit_price: element.producto.precio
            });
          });

          items.push({
            title: 'Envio',
              description: 'Concepto de transporte y logistica',
              quantity: 1,
              currency_id: 'PEN',
              unit_price: this.envio
          });

          if(this.venta.cupon){
            items.push({
              title: 'Descuento',
                description: 'Cupón aplicado ' + this.venta.cupon,
                quantity: 1,
                currency_id: 'PEN',
                unit_price: -(this.descuento)
            });
          }

          let data = {
            notification_url: 'https://hookb.in/6JlGBe8MYbsoRnwwRd1Z',
            items: items,
            back_urls: {
                failure: "http://localhost:5000/verificar-pago/failure/"+this.direccion_envio._id+'/'+this.venta.cupon+'/'+this.envio+'/'+this.tipo_descuento+'/'+this.valor_descuento+'/'+this.total_pagar+'/'+this.subtotal,
                pending: "http://localhost:5000/verificar-pago/pending/"+this.direccion_envio._id+'/'+this.venta.cupon+'/'+this.envio+'/'+this.tipo_descuento+'/'+this.valor_descuento+'/'+this.total_pagar+'/'+this.subtotal,
                success: "http://localhost:5000/verificar-pago/success/"+this.direccion_envio._id+'/'+this.venta.cupon+'/'+this.envio+'/'+this.tipo_descuento+'/'+this.valor_descuento+'/'+this.total_pagar+'/'+this.subtotal,
            },
            auto_return: "approved"
          }


          this._guestService.createToken(data).subscribe(
            response=>{
              console.log(response);
              window.location.href =response.sandbox_init_point;

            }
          );
        }else{
          iziToast.show({
              title: 'ERROR',
              titleColor: '#FF0000',
              color: '#FFF',
              class: 'text-danger',
              position: 'topRight',
              message: response.message
          });
          this.btn_load = false;
        }
      }
    );
  }

  generar_pedido(tipo:any){
    this.venta.transaccion = 'Venta pedido';
    if(this.currency != 'PEN'){
      this.venta.currency = 'USD';
    }else{
      this.venta.currency = 'PEN';
    }
    this.venta.subtotal = this.subtotal;
    this.venta.total_pagar = this.total_pagar;
    this.venta.envio_precio = this.envio;
    this.venta.detalles = this.dventa;
    this.venta.metodo_pago = this.metodo_pago;
    this.venta.nota = this.nota;
    this.venta.direccion = this.direccion_envio._id;
    this.venta.tipo_descuento = this.tipo_descuento;
    this.venta.valor_descuento = this.valor_descuento;
    let idcliente = localStorage.getItem('_id');
    this.venta.cliente = idcliente;
    console.log(this.venta);

    this.btn_load = true;
    this._guestService.pedido_compra_cliente(this.venta,this.token).subscribe(
      response=>{
        if(response.venta != undefined){
          this.btn_load = false;
          this._router.navigate(['/cuenta/pedidos']);
        }else{
          iziToast.show({
              title: 'ERROR',
              titleColor: '#FF0000',
              color: '#FFF',
              class: 'text-danger',
              position: 'topRight',
              message: response.message
          });
          this.btn_load = false;
        }
      }
    );
  }


  validar_cupon(){

    if(this.venta.cupon){
      if(this.venta.cupon.toString().length <= 25){

        this._guestService.validar_cupon_admin(this.venta.cupon,this.token).subscribe(
          response=>{
            console.log(response);

            if(response.data != undefined){
              this.tipo_descuento =  response.data.tipo;

              if(response.data.disponibilidad == 'Exterior'){
                if(this.currency != 'PEN'){
                  if(response.data.tipo == 'Valor fijo'){
                    this.descuento = response.data.valor;
                    this.valor_descuento = this.descuento;
                    this.total_pagar = (this.total_pagar_const - this.descuento) + this.envio;
                  }else if(response.data.tipo == 'Porcentaje'){
                    this.descuento =Math.round((this.total_pagar_const * response.data.valor)/100);
                    this.valor_descuento = this.descuento;
                    this.total_pagar = (this.total_pagar_const - this.descuento) + this.envio;
                  }
                  this.select_direccion_envio(this.direccion_envio);
                }else{
                  iziToast.show({
                      title: 'ERROR',
                      titleColor: '#ff0000',
                      color: '#FFF',
                      class: 'text-success',
                      position: 'topRight',
                      message: 'El cupón no es valido para tu país'
                  });
                }
              }else if(response.data.disponibilidad == 'Peru'){
                if(this.currency == 'PEN'){
                  if(response.data.tipo == 'Valor fijo'){
                    this.descuento = response.data.valor;
                    this.valor_descuento = this.descuento;
                    this.total_pagar = (this.total_pagar_const - this.descuento) + this.envio;
                  }else if(response.data.tipo == 'Porcentaje'){
                    this.descuento =Math.round((this.total_pagar_const * response.data.valor)/100);
                    this.valor_descuento = this.descuento;
                    this.total_pagar = (this.total_pagar_const - this.descuento) + this.envio;
                  }
                  this.select_direccion_envio(this.direccion_envio);
                }else{
                  iziToast.show({
                      title: 'ERROR',
                      titleColor: '#ff0000',
                      color: '#FFF',
                      class: 'text-success',
                      position: 'topRight',
                      message: 'El cupón no es valido para tu país'
                  });
                }
              }

            }else{
              iziToast.show({
                  title: 'ERROR',
                  titleColor: '#ff0000',
                  color: '#FFF',
                  class: 'text-success',
                  position: 'topRight',
                  message: response.message
              });
            }
          }
        );
      }else{
        iziToast.show({
            title: 'ERROR',
            titleColor: '#ff0000',
            color: '#FFF',
            class: 'text-success',
            position: 'topRight',
            message: 'El cupon debe ser menos de 25 caracteres.'
        });
      }
    }else{
      iziToast.show({
          title: 'ERROR',
          titleColor: '#ff0000',
          color: '#FFF',
          class: 'text-success',
          position: 'topRight',
          message: 'El cupon no es valido.'
      });

    }
  }


}
<app-nav></app-nav>
<div class="ps-page--default">
    <div class="container">
      <div class="ps-page__header">
        <h3>Checkout</h3>
        <ul class="ps-breadcrumb">
          <li><a [routerLink]="['/']">Home</a></li>
          <li>Checkout</li>
        </ul>
      </div>
      <div class="ps-page__content">
        <div class="ps-checkout">

            <div class="row">
                <div class="col-md-6">
                  <ng-container *ngIf="direcciones.length == 0">
                      <div class="ps-form__billings">
                          <h4 class="ps-form__heading">Nueva dirección</h4>
                          <form #registroForm="ngForm" (ngSubmit)="registrar(registroForm)">
                            <div class="row">
                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">Nombres</label>
                                    <input type="text" required class="form-control form-control-lg" name="nombres" [(ngModel)]="direccion.nombres" placeholder="Nombres completos">
                                </div>
                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">Apellidos</label>
                                    <input type="text" required class="form-control form-control-lg" name="apellidos" [(ngModel)]="direccion.apellidos" placeholder="Apellidos completos">
                                </div>
                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">Documento de Identidad
                                        <span *ngIf="currency != 'PEN'"> (Opcional)</span>
                                    </label>
                                    <input *ngIf="currency == 'PEN'" type="text" required class="form-control form-control-lg" name="dni" [(ngModel)]="direccion.dni" placeholder="Documento de Identidad">
                                    <input *ngIf="currency != 'PEN'" type="text" class="form-control form-control-lg" name="dni" [(ngModel)]="direccion.dni" placeholder="Documento de Identidad">
                                </div>
                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">ZIP
                                        <span *ngIf="currency == 'PEN'"> (Opcional)</span>
                                    </label>
                                    <input *ngIf="currency != 'PEN'" type="text" required class="form-control form-control-lg" name="zip" [(ngModel)]="direccion.zip" placeholder="Código postal">
                                    <input *ngIf="currency == 'PEN'" type="text" class="form-control form-control-lg" name="zip" [(ngModel)]="direccion.zip" placeholder="Código postal">
                                </div>
                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">Telefono</label>
                                    <input type="text" required class="form-control form-control-lg" name="telefono" [(ngModel)]="direccion.telefono" placeholder="Telefono">
                                </div>
                                <div class="col-sm-12 form-group">
                                    <label for="ac-fn">Dirección</label>
                                    <textarea required class="form-control" placeholder="Direccion" name="direccion" [(ngModel)]="direccion.direccion" rows="3"></textarea>
                                </div>
                                <div class="col-sm-12 form-group">
                                    <label for="ac-fn">Referencia
                                        <span *ngIf="currency != 'PEN'"> (Opcional)</span>
                                    </label>
                                    <textarea *ngIf="currency == 'PEN'" type="text" required class="form-control" placeholder="Referencia" name="referencia" [(ngModel)]="direccion.referencia" rows="2"></textarea>
                                    <textarea *ngIf="currency != 'PEN'" type="text" class="form-control" placeholder="Referencia" name="referencia" [(ngModel)]="direccion.referencia" rows="2"></textarea>
                                </div>


                                <div class="col-sm-6 form-group">
                                    <label for="ac-fn">País</label>
                                    <select class="form-control" name="str_pais" required [(ngModel)]="str_pais" (change)="select_pais()">
                                        <option value="" selected disabled>Seleccionar</option>
                                        <ng-container *ngFor="let item of zonas">
                                            <ng-container  *ngIf="currency =='PEN'">
                                                <option value="{{item.country}}_{{item.zona}}" *ngIf="item.country =='Perú'">{{item.country}}</option>
                                            </ng-container>
                                            <ng-container  *ngIf="currency !='PEN'">
                                                <option value="{{item.country}}_{{item.zona}}" *ngIf="item.country !='Perú'">{{item.country}}</option>
                                            </ng-container>

                                        </ng-container>


                                    </select>
                                </div>
                                <div class="col-sm-6 form-group" *ngIf="direccion.pais != 'Perú'">
                                    <label for="ac-fn">Distrito/Suburbio</label>
                                    <input type="text" required class="form-control form-control-lg" name="distrito" [(ngModel)]="direccion.distrito" placeholder="Ciudad">
                                </div>
                                <div class="col-sm-6 form-group" *ngIf="direccion.pais == 'Perú'">
                                    <label for="ac-fn">Región</label>
                                    <select id="sl-region"  name="region" [(ngModel)]="direccion.region" class="form-control" disabled (change)="select_region()">
                                        <option value="" selected disabled>Seleccionar</option>
                                        <option value="{{item.id}}" *ngFor="let item of regiones">{{item.name}}</option>
                                    </select>
                                </div>
                                <div class="col-sm-6 form-group" *ngIf="direccion.pais == 'Perú'">
                                    <label for="ac-fn">Provincia</label>
                                    <select id="sl-provincia" name="provincia" [(ngModel)]="direccion.provincia" class="form-control" disabled (change)="select_provincia()">
                                        <option value="" selected disabled>Seleccionar</option>
                                        <option value="{{item.id}}" *ngFor="let item of provincias">{{item.name}}</option>
                                    </select>
                                </div>
                                <div class="col-sm-6 form-group" *ngIf="direccion.pais == 'Perú'">
                                    <label for="ac-fn">Distrito</label>
                                    <select id="sl-distrito" name="distrito" [(ngModel)]="direccion.distrito" class="form-control" disabled>
                                        <option value="" selected disabled>Seleccionar</option>
                                        <option value="{{item.id}}" *ngFor="let item of distritos">{{item.name}}</option>
                                    </select>
                                </div>

                                <div class="col-sm-12">
                                  <button type="submit" class="ps-btn ps-btn--fullwidth ps-btn--black">Crear dirección</button>
                                </div>

                            </div>
                          </form>
                      </div>
                  </ng-container>
                  <ng-container  *ngIf="direcciones.length >= 1">
                      <h4 class="ps-form__heading" style="margin-bottom: 2rem!important;">Mis direcciones</h4>

                      <div class="card mb-3" *ngFor="let item of direcciones">
                          <div class="card-body">
                              <figure>
                                  <figcaption>{{item.destinatario}}</figcaption>
                                  <span class="d-block mb-3 font-size-sm text-muted">{{item.zip}}</span>

                                  <div class="row">
                                      <div class="col" *ngIf="item.pais == 'Perú'">
                                          <p>
                                              {{item.direccion}}
                                              <ng-container *ngIf="item.referencia">
                                                  <br>
                                                  {{item.referencia}}
                                              </ng-container>

                                          </p>
                                          <p>
                                            {{item.pais}}, {{item.region}}
                                           <br>
                                            <span>{{item.provincia}}, {{item.distrito}}</span>
                                          </p>
                                      </div>
                                      <div class="col" *ngIf="item.pais != 'Perú'">
                                          <p>
                                              {{item.direccion}}
                                          </p>
                                          <p>
                                          {{item.pais}}, {{item.distrito}}
                                          </p>

                                      </div>
                                      <ng-container *ngIf="currency != 'PEN'">
                                        <a *ngIf="item.pais != 'Perú'" class="text-success mt-4" style="cursor: pointer;margin-right: 2rem;" (click)="select_direccion_envio(item)">
                                            [Seleccionar]
                                        </a>
                                        <a *ngIf="item.pais == 'Perú'" class="text-danger mt-4" style="cursor: pointer;margin-right: 2rem;cursor: not-allowed;">
                                            [Seleccionar]
                                        </a>
                                      </ng-container>
                                      <ng-container *ngIf="currency == 'PEN'">
                                        <a *ngIf="item.pais == 'Perú'" class="text-success mt-4" style="cursor: pointer;margin-right: 2rem;" (click)="select_direccion_envio(item)">
                                            [Seleccionar]
                                        </a>
                                        <a *ngIf="item.pais != 'Perú'" class="text-danger mt-4" style="cursor: pointer;margin-right: 2rem;cursor: not-allowed;">
                                            [Seleccionar]
                                        </a>
                                      </ng-container>
                                  </div>
                              </figure>
                          </div>
                      </div>

                        <figure style="margin-top: 30px;">
                            <a class="ps-btn ps-btn--outline" [routerLink]="['/cuenta/direcciones']" >Nueva dirección</a>

                        </figure>
                  </ng-container>
                  <div class="ps-shopping-cart" style="margin-top: 5rem;">
                    <div class="ps-section__actions">
                        <div class="form-group">
                            <label style="color: #dc1d61 !important;">Cupón de descuento</label>
                            <div class="form-group__content">
                                <input class="form-control" type="text" placeholder="Ingresa tu código" name="cupon" [(ngModel)]="venta.cupon">
                                <a style="cursor: pointer;" (click)="validar_cupon()" >Aplicar</a>
                            </div>
                        </div>
                    </div>
                  </div>


                </div>
                <div class="col-md-6">
                  <div class="ps-form__orders">
                    <h3>Tu orden</h3>
                    <div class="ps-block--checkout-order">

                      <div class="ps-block__content">
                        <figure>
                          <figcaption><strong>Producto</strong><strong>Subtotal</strong></figcaption>
                        </figure>
                        <figure class="ps-block__items">
                            <a [routerLink]="['/productos/',item.producto.slug]" *ngFor="let item of carrito_arr" style="font-size: 14px !important;">
                                <div class="ps-product--sidebar">
                                    <div class="ps-product__thumbnail" style="max-width: 40px;"><a class="ps-product__overlay" [routerLink]="['/productos/',item.producto.slug]"></a>
                                        <img [src]="url +'obtener_portada/'+item.producto.portada" alt="">
                                    </div>
                                    <div class="ps-product__content">
                                        <a class="ps-product__title" href="product-default.html" style="padding: 0px;">{{item.producto.titulo}}
                                            <span  style="margin-bottom: 0px;"> &nbsp;x{{item.cantidad}}</span></a>
                                        <p  style="margin-bottom: 0px;"> Tallas: {{item.variedad.valor}}</p>
                                    </div>
                                </div>





                                <small style="padding-top: 1rem;">
                                    <ng-container *ngIf="currency != 'PEN'">
                                        ${{item.producto.precio_dolar*item.cantidad}}
                                    </ng-container>
                                    <ng-container *ngIf="currency == 'PEN'">
                                        S/{{item.producto.precio*item.cantidad}}
                                    </ng-container>
                                </small>


                            </a>
                        </figure>
                        <figure>
                          <figcaption><strong>Subtotal</strong>
                            <small>
                                <span *ngIf="currency != 'PEN'">${{subtotal}}</span>
                                <span *ngIf="currency == 'PEN'">S/{{subtotal}}</span>
                            </small>
                        </figcaption>
                        </figure>
                        <figure class="ps-block__shipping">
                          <h3>Envio</h3>
                          <p>Selecciona la la dirección de envio</p>
                        </figure>

                        <figure class="ps-block__total">
                            <figcaption style="margin-top: 2rem;">
                                <span *ngIf="!envio_gratis">Envio</span>
                                <span *ngIf="envio_gratis" style="color: #e70a62;"><b>Envio gratis</b></span>
                                <span>
                                    <span *ngIf="currency != 'PEN'">${{envio}}</span>
                                <span *ngIf="currency == 'PEN'">S/{{envio}}</span>
                                </span>
                            </figcaption>
                        </figure>
                        <figure class="ps-block__total">
                            <figcaption style="margin-top: 2rem;">Descuento
                                <span>
                                    <span *ngIf="currency != 'PEN'">- ${{descuento}}</span>
                                <span *ngIf="currency == 'PEN'">- S/{{descuento}}</span>
                                </span>
                            </figcaption>
                        </figure>
                        <figure class="ps-block__total">
                          <figcaption>Total
                              <span>
                                <span *ngIf="currency != 'PEN'">${{total_pagar}}</span>
                                <span *ngIf="currency == 'PEN'">S/{{total_pagar}}</span>
                              </span>
                            </figcaption>
                        </figure>
                        <figure class="ps-block__payment-methods">
                            <ng-container *ngIf="currency == 'PEN'">
                                <div class="ps-radio" style="display: flex; justify-content: space-between;">
                                    <input class="form-control" type="radio" id="shipping-3" name="metodo_pago" [(ngModel)]="metodo_pago" value="Tarjeta de crédito">
                                    <label for="shipping-3" class="text-muted"><b>Tarjeta de crédito</b></label>

                                    <img src="assets/img/payments.png" style="width: 130px;">
                                </div>
                                <div class="ps-radio">
                                    <input class="form-control" type="radio" id="shipping-1" name="metodo_pago" [(ngModel)]="metodo_pago" value="Yape o Plin">
                                    <label for="shipping-1" class="text-muted"><b>¡Paga al instante con Yape o Plin!</b></label>

                                    <p *ngIf="metodo_pago == 'Yape o Plin'" style="font-size: 13px;margin-bottom: 2.3rem;">(Luego de hacer click en "PROCESAR PEDIDO" podrás visualizar nuestros datos de pago).</p>
                                </div>
                                <div class="ps-radio">
                                    <input class="form-control" type="radio" id="shipping-2" name="metodo_pago" [(ngModel)]="metodo_pago" value="Transferencia o Deposito">
                                    <label for="shipping-2" class="text-muted"><b>Transferencia o Deposito</b></label>

                                    <p *ngIf="metodo_pago == 'Transferencia o Deposito'" style="font-size: 13px">(Luego de hacer click en "PROCESAR PEDIDO" podrás visualizar nuestros datos de pago).</p>
                                </div>

                            </ng-container>

                        <p>Deberes e impuestos no están incluidos en paquetes con envio internacional. /Tus datos personales serán utilizados para procesar tu pedido y respaldar tu experiencia en este sitio web.</p>
                        <a [routerLink]="['/terminos-condiciones']"> <b> Términos y privacidad.</b></a>
                        </figure>
                      </div>
                      <div class="ps-block__footer">
                        <ng-container *ngIf="direccion_envio != undefined">
                            <ng-container *ngIf="carrito_arr.length >= 1">
                              <ng-container *ngIf="currency == 'PEN'">
                                <div class="ps-block__bottom" *ngIf="metodo_pago == 'Tarjeta de crédito'">
                                  <a *ngIf="!btn_load" class="ps-btn ps-btn--black" id="btnBuy" style="cursor: pointer;" (click)="get_token_culqi()">Procesar pago</a>
                                  <button *ngIf="btn_load" class="ps-btn ps-btn--black btnBuy" style="cursor: pointer;" disabled>Procesando...</button>
                                </div>
                                <div class="ps-block__bottom" *ngIf="metodo_pago == 'Yape o Plin'">
                                  <a *ngIf="!btn_load" class="ps-btn ps-btn--black" id="btnBuy" style="cursor: pointer;" (click)="generar_pedido('Yape o Plin')">Enviar pedido</a>
                                  <button *ngIf="btn_load" class="ps-btn ps-btn--black btnBuy" style="cursor: pointer;" disabled>Procesando...</button>
                                </div>
                                <div class="ps-block__bottom" *ngIf="metodo_pago == 'Transferencia o Deposito'">
                                  <a *ngIf="!btn_load" class="ps-btn ps-btn--black" id="btnBuy" style="cursor: pointer;" (click)="generar_pedido('Transferencia o Deposito')">Enviar pedido</a>
                                  <button *ngIf="btn_load" class="ps-btn ps-btn--black btnBuy" style="cursor: pointer;" disabled>Procesando...</button>
                                </div>
                              </ng-container>

                            </ng-container>
                        </ng-container>

                        <div class="pt-3 pb-0 contpaypal" #paypalButton>

                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
        </div>
      </div>
    </div>
  </div>
<app-footer></app-footer>
